const { promisify } = require("util")
const jwt = require("jsonwebtoken")
const AppError = require("../utils/appError")
const User = require("../models/userModel")
const catchAsync = require("../utils/catchAsync")
const sendEmail = require("../utils/email")
const crypto = require("crypto")

const signToken = id => {
    return jwt.sign({ id: id }, process.env.JWT_SECRET, {
        expiresIn: process.env.JWT_EXPIRES_IN
    })
}


const createSendToken = (user, statusCode, res) => {
    const token = signToken(user._id)
    const cookieOptions = {
        expires: new Date(Date.now() + process.env.JWT_COOKIE_EXPIRES_IN * 24 * 60 * 60 * 1000),
        httpOnly: true //cannot be accessed or modified ina ny way

    }
    if (process.env.NODE_ENV === "production") cookieOptions.secure = true
    //cookies are text that web browsers use to comms with server as the server first send a cookie to the 
    //browser then the browser sends that cookie in every request to the server
    //name is the unique identifier for a cookie. it gets replaced
    res.cookie("jwt", token, cookieOptions)

